#' Variable Tree
#'
#' Function for Generating Tree Summaries of Variables.
#'
#' @examples
#' \dontrun{
#' # example will be added
#'}
#' @param data The data as a data frame.
#' @param vars .
#' @param percvar .
#' @param percvarLevel .
#' @param summaryvar .
#' @param excl .
#' @param vp .
#' @param horizontal .
#' @param sline .
#' @param varnames .
#' @param nodelabel .
#' @param pct .
#' @param showcount .
#' @param legend .
#' @param mytitle .
#' @param width .
#' @param height .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$todo} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$text1} \tab \tab \tab \tab \tab a html \cr
#' }
#'
#' @export
vartree <- function(
    data,
    vars,
    percvar,
    percvarLevel,
    summaryvar,
    excl = TRUE,
    vp = TRUE,
    horizontal = FALSE,
    sline = TRUE,
    varnames = FALSE,
    nodelabel = TRUE,
    pct = FALSE,
    showcount = TRUE,
    legend = FALSE,
    mytitle = "Variable Tree",
    width = 1000,
    height = 1000) {

    if ( ! requireNamespace('jmvcore'))
        stop('vartree requires jmvcore to be installed (restart may be required)')

    if ( ! missing(vars)) vars <- jmvcore::resolveQuo(jmvcore::enquo(vars))
    if ( ! missing(percvar)) percvar <- jmvcore::resolveQuo(jmvcore::enquo(percvar))
    if ( ! missing(summaryvar)) summaryvar <- jmvcore::resolveQuo(jmvcore::enquo(summaryvar))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(vars), vars, NULL),
            `if`( ! missing(percvar), percvar, NULL),
            `if`( ! missing(summaryvar), summaryvar, NULL))

    for (v in vars) if (v %in% names(data)) data[[v]] <- as.factor(data[[v]])
    for (v in percvar) if (v %in% names(data)) data[[v]] <- as.factor(data[[v]])

    options <- vartreeOptions$new(
        vars = vars,
        percvar = percvar,
        percvarLevel = percvarLevel,
        summaryvar = summaryvar,
        excl = excl,
        vp = vp,
        horizontal = horizontal,
        sline = sline,
        varnames = varnames,
        nodelabel = nodelabel,
        pct = pct,
        showcount = showcount,
        legend = legend,
        mytitle = mytitle,
        width = width,
        height = height)

    analysis <- vartreeClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}
